const shuf = a => { const b=[...a]; for(let i=b.length-1;i>0;i--){const j=0|Math.random()*(i+1);[b[i],b[j]]=[b[j],b[i]];} return b; };
const shuffleOpts = q => { const idxs = q.o.map((_,i)=>i); const si = shuf(idxs); return {...q, o: si.map(i=>q.o[i]), a: si.indexOf(q.a) }; };
const getQs = (cat,br) => { const bk=Q[cat]; if(!bk)return[]; const all=bk.all||[]; const brQ=br&&br!=="all"&&bk[br]?bk[br]:[]; if(br&&br!=="all") return [...brQ,...all.filter(q=>q.b===br||q.b==="general")]; return [...all,...Object.entries(bk).filter(([k])=>k!=="all").flatMap(([,v])=>v)]; };
const getAllQs = () => Object.keys(Q).flatMap(k=>getQs(k,"all"));
const smartQs = (cat,br,n,wrong) => { const pool=cat==="super"?getAllQs():getQs(cat,br); const wIds=new Set(wrong||[]); const w=pool.filter((_,i)=>wIds.has(`${cat}_${br}_${i}`)); const r=pool.filter((_,i)=>!wIds.has(`${cat}_${br}_${i}`)); const wc=Math.min(Math.floor(n*0.4),w.length); return shuf([...shuf(w).slice(0,wc),...shuf(r).slice(0,n-wc)]).map(shuffleOpts); };
const ld = (k,f) => { try{const s=localStorage.getItem(k);return s?JSON.parse(s):f;}catch{return f;} };
const sv = (k,v) => { try{localStorage.setItem(k,JSON.stringify(v));}catch{} };
const getStreak = h => { if(!h.length)return 0; const today=new Date().toDateString(); const days=[...new Set(h.map(x=>new Date(x.date).toDateString()))].sort((a,b)=>new Date(b)-new Date(a)); if(days[0]!==today&&days[0]!==new Date(Date.now()-864e5).toDateString())return 0; let s=0,ck=days[0]===today?new Date():new Date(Date.now()-864e5); for(const d of days){if(new Date(d).toDateString()===ck.toDateString()){s++;ck=new Date(ck-864e5);}else break;} return s; };

